package ch.bbw.zork;/* * author:  Michael Kolling, Version: 1.0, Date: July 1999 * refactoring: Rinaldo Lanza, September 2020 */import java.util.Arrays;import java.util.List;public class CommandWords {	private List<String> validCommands = Arrays.asList("go", "quit", "help" );	private List<String> validSecondCommands = Arrays.asList("north", "east", "south", "west", "back");	public boolean isCommand(String commandWord) {		return validCommands.stream()				.filter(c -> c.equals(commandWord))				.count()>0;	}	public boolean isSecondCommandValid(String commandWord){		return validSecondCommands.stream()				.filter(c -> c.equals(commandWord))				.count()>0;	}	public String showAll() {		return String.join(" ", validCommands);	}}